checaletra: ; mov di,checa
  xor cx,cx
  mov [aux], cl

  .loop1:
    
    cmp cl,[index]
    je .letreca
    inc di
    inc cl
    jmp .loop1
  
  
  .letreca:
    xor cx,cx
    cmp byte[di],97
    je .soma
    jmp .acab
  
  .soma:
    mov byte[di],98
    mov cl, 1
    mov [aux], cl



  .acab:
    

  ret

