getchar:
  mov ah, 0x00
  int 16h
ret
  
delchar:
  mov al, 0x08          ; backspace
  call coloca
  mov al, ' '
  call coloca
  mov al, 0x08          ; backspace
  call coloca
ret
  
endl:
  mov al, 0x0a          ; line feed
  call coloca
  mov al, 0x0d          ; carriage return
  call coloca
ret



gets:                 ; mov di, string
  xor cx, cx          ; zerar contador
  .loop1:
    call getchar
    cmp al, 0x08      ; backspace
    je .backspace
    cmp al, 0x0d      ; carriage return
    je .done
    cmp cl, 10        ; string limit checker
    je .loop1
    
    stosb
    inc cl
    call coloca
    
    jmp .loop1
    .backspace:
      cmp cl, 0       ; is empty?
      je .loop1
      dec di
      dec cl
      mov byte[di], 0
      call delchar
    jmp .loop1
  .done:
  mov al, 0
  stosb
  call endl
ret

strcmp:              ; mov si, string1, mov di, string2
  .loop1:
    lodsb
    mov bl, 96
    cmp byte[di],bl
    jg .minusc
    mov bl,0
    cmp byte[di],bl
    je .minusc
    add byte[di],32

    .minusc:
    cmp al, byte[di]
    jne .notequal
    cmp al, 0
    je .equal
    inc di
    jmp .loop1
  .notequal:
    clc
    call errou   
    ret
  .equal:
    stc
    call acertou  
ret 
